import React, { useState } from 'react';
import PerformanceList from '../components/Performance/PerformanceList';
import PerformanceForm from '../components/Performance/PerformanceForm';

const PerformancePage = () => {
  const [selectedAthleteId, setSelectedAthleteId] = useState(null);
  const [showForm, setShowForm] = useState(false);

  const handleAddClick = () => {
    if (!selectedAthleteId) {
      alert('Please select an athlete first');
      return;
    }
    setShowForm(true);
  };

  const handleFormClose = () => {
    setShowForm(false);
  };

  // You can add athlete selection UI here
  // For simplicity, we hard-code an athleteId or integrate with athlete selection from AthletePage

  return (
    <div>
      <h2>Performance Page</h2>
      <button onClick={handleAddClick}>Add Performance</button>
      <PerformanceList athleteId={selectedAthleteId} />
      {showForm && <PerformanceForm athleteId={selectedAthleteId} onClose={handleFormClose} />}
    </div>
  );
};

export default PerformancePage;
